import React, { useEffect, useState } from 'react';
import { Text, View } from 'react-native';
import { AppLoading } from 'expo';
import * as Font from 'expo-font';
import { bindActionCreators } from 'redux';
import { connect } from 'react-redux';
import Main from './app/screen/MainScreen';
import * as weatherActions from '../actions';

function useFonts(fontMap) {
  let [fontsLoaded, setFontsLoaded] = useState(false);
  (async () => {
    await Font.loadAsync(fontMap);
    setFontsLoaded(true);
  })();
  return [fontsLoaded];
}
let [fontsLoaded] = useFonts({
    'montserrat-700': require('./app/assets/fonts/montserrat-700.ttf'),
'montserrat-regular': require('./app/assets/fonts/montserrat-regular.ttf'),
'verdana-regular': require('./app/assets/fonts/verdana-regular.ttf')
  });
export class WeatherApp extends Component {

  static propTypes = {
 }

 componentDidMount() {
     navigator.geolocation.getCurrentPosition( // eslint-disable-line
         (position) => {
             const lat = position.coords.latitude.toString();
             const lon = position.coords.longitude.toString();
             this.props.actions.searchByCoordinates(lat, lon);
         },
         () => {
             const errorMessage = 'Could not fetch weather for your location';
             this.props.actions.setErrorMessage(errorMessage);
         },
         { enableHighAccuracy: true, timeout: 20000, maximumAge: 1000 });
 }
  render(){
    const { state, actions } = this.props;
    const { weatherData, errorMessage, isLoading, isFahrenheit } = state;
    if (!fontsLoaded) {
    return <AppLoading />;
  } else {
    return (
     <Main/>
    );
  }
  }
};


export default connect(state => ({
  state,
}),
dispatch => ({
  actions: bindActionCreators(weatherActions, dispatch),
}),
)(WeatherApp);
